package perulangan;

public class Perulangan2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//		inisiasi variabel a = 0 dengan tipe data integer (int)
		int a=0;        
//		lakukan cetak statemen
		do{
//			statement
			System.out.println("Nilai a : "+a);
//			perulangan
			a++;
//			kondisi jika a<5 lakukan perulangan
		}while(a<5);

	}

}
